import React, { useState, useEffect } from 'react';
import { User, Profile, Settings } from '../types/user';
import { ApiService } from '../services/api';
import { ValidationUtils } from '../utils/validation';

interface UserProfile1Props {
  userId: string;
  onUpdate?: (profile: Profile) => void;
  settings?: Settings;
}

export const UserProfile1: React.FC<UserProfile1Props> = ({ 
  userId, 
  onUpdate, 
  settings 
}) => {
  const [profile, setProfile] = useState<Profile | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const fetchProfile = async () => {
      try {
        setLoading(true);
        const data = await ApiService.getUserProfile(userId);

        if (!ValidationUtils.isValidProfile(data)) {
          throw new Error('Invalid profile data received');
        }

        setProfile(data);
        onUpdate?.(data);
      } catch (err) {
        setError(err instanceof Error ? err.message : 'Unknown error');
      } finally {
        setLoading(false);
      }
    };

    if (userId) {
      fetchProfile();
    }
  }, [userId, onUpdate]);

  const handleProfileUpdate = async (updates: Partial<Profile>) => {
    try {
      const updatedProfile = await ApiService.updateProfile(userId, updates);
      setProfile(updatedProfile);
      onUpdate?.(updatedProfile);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Update failed');
    }
  };

  if (loading) return <div>Loading profile...</div>;
  if (error) return <div>Error: {error}</div>;
  if (!profile) return <div>No profile found</div>;

  return (
    <div className="user-profile-1">
      <h2>{profile.name}</h2>
      <p>{profile.email}</p>
      <button onClick={() => handleProfileUpdate({ lastSeen: new Date() })}>
        Update Last Seen
      </button>
    </div>
  );
};


